declare @retailerid uniqueidentifier
declare @storeid uniqueidentifier
declare @addressid uniqueidentifier
declare @posid uniqueidentifier
declare @openFrom varchar(100)
declare @openTo varchar(100)

set @retailerid=convert(uniqueidentifier, '${retailer_id}')
set @posid=convert(uniqueidentifier, '${posid}')
set @storeid=newid()
set @addressid=newid()
set @openFrom='{"ticks": 0}'
set @openTo='{"ticks": 0}'


exec Broker._Setup_RegisterStore 
    @param_scanexpiration=10080, 
    @param_RetailerID='${retailer_id}',
    @param_StoreID=@storeid,
    @param_addressID=@addressid,
    @param_add1='${add1}',
    @param_add2='${add2}',
    @param_city='${city}',
    @param_state='${state}',
    @param_zip='${zip}',
    @param_country='${country}',
    @param_lat=${lat},
    @param_lon=${lon},
    @param_timezoneid='${timezoneid}',
    @param_storeName='${storeName}',
    @param_email='${email}',
    @param_phone='${phone}',
    @param_openFrom=@openFrom,
    @param_openTo=@openTo,
    @param_daysOfWeek=127,
    @param_posstorekey=${posstorekey},
    @param_POSID='${posid}'
